DELIMITER $$
CREATE TRIGGER `before_comments_delete` BEFORE DELETE ON `comment` FOR EACH ROW BEGIN
    INSERT INTO comment_log
    VALUES(OLD.id,OLD.user,OLD.doctor,OLD.post,OLD.content,OLD.date_share);
END
$$
DELIMITER ;

