INSERT INTO `post` (`id`, `user`, `content`, `img`, `date_share`, `deptno`, `num_search`) VALUES
(96, 52, 'من يصاب بتسوس الاسنان؟', NULL, '2021-05-24', 33, 6),
(97, 80, 'كيف اعرف انني مصاب بتسوس الاسنان؟', NULL, '2021-05-24', 33, 2),
(98, 2, 'هل يوجد عمق معين الذي تتكون فيه \"جيوب\" اللثة التي تستلزم اجراء الجراحة؟', NULL, '2021-05-24', 33, 2),
(99, 75, 'ماذا ينبغي علي ان افعل لمنع تفاقم مرض السكري؟', NULL, '2021-05-24', 31, 27),
(100, 1, 'ما هو سبب حدوث سرطان الغدة الدرقية؟', NULL, '2021-05-24', 30, 4),
(101, 77, 'اي انواع الطعام تسبب حدوث الصداع؟', NULL, '2021-05-24', 50, 6),
(102, 74, 'كيف تتم معالجة البواسير؟', NULL, '2021-05-24', 50, 4),
(103, 73, 'كيف يمكنني الوقاية من النزلات البردية؟', NULL, '2021-05-24', 50, 5),
(104, 87, 'قبل شهر أصيبت ابنتي البالغة من العمر 7 شهور بجدري الماء. والان لا يزال لديها بعض القروح حول الفم وندبات في أماكن مختلفة من الوجة. ما هو الاحتمال بأن تختفي هذه العلامات؟', NULL, '2021-05-24', 16, 1),
(105, 52, 'أنا امراة في سن الـ 62عاماً، أعاني منذ سنوات من حكه شديدة جدا في المستقيم. لا يوجد لدي أي بواسير، تم تشخيص مرض جلدي لا أعرف اسمه. تلقيت العلاج بواسطة مرهم ديرموفيت (Clobetasol propionate) دون نتائج. أنا أخجل من الذهاب الى الطبيب، ماذا يمكن أن أفعل؟', NULL, '2021-05-24', 16, 1),
(106, 52, 'ارتفاع السكر في الدم', NULL, '2021-05-24', 31, 1),
(107, 74, 'ما هي أعراض مرض كوفيد-19؟', NULL, '2021-05-27', 50, 4),
(108, 79, 'كيف لي أن أعرف ما إذا كنت مصابا بفيروس كورونا أو مجرد نزلة برد شائعة؟', NULL, '2021-05-09', 50, 1),
(109, 72, 'عندي ابني يبلغ من ٧سنوت يكون البراز عنده بني طويل لين ممكن يلبغ ٢٦سنتي هل يعتبر طبيعي البراز طويل', NULL, '2021-05-28', 1, 2),
(110, 76, 'ابني عمره سنتين اصيب بالتهابات وصف له الطبيب دواء اومجين مضاد حيوي لانه كان عنده حراره وبعد ايام من الاستعمال الدواء اصيب بالاسهال ولكن لون البراز اسود او بني غامق ارجوكم اجيب\r\n\r\n', NULL, '2021-05-28', 1, 4),
(111, 46, 'ابني عمره سنتين من فتره عملتله تحاليل طلع مع فقر دم بسيط ونقص بالحديد واعطاه دكتور نقوط واله يومين ارتفاع حراره واستفراغ مستمر وشعور بالغطش شديد واسهال ولون برازه اسود\r\n\r\n', NULL, '2021-05-29', 1, 2),
(112, 86, 'السلام عليكم اختي عصبيه جدآ ، وعمرها ١٧ و وقت العصبية عند صراخها يخرج من فمها تزبيد هل له اضرار ،وايش اسبابة\r\n\r\n', NULL, '2021-05-28', 2, 2),
(113, 89, 'اول مره يجيني تنميل في اليدين والخشم ومنطقة الفم كان في وقت عصبية بس هل هذا طبيعي ؟', NULL, '2021-05-26', 2, 2),
(114, 1, 'من ستة أشهر حصل انقطاع ف يدي اليمني 6 اوتره وعصبان وشريان وتم الحمدلله عمل عمليه بنجا ح ولانك الان يووجد تنميل ف اليد\r\n', NULL, '2021-05-19', 2, 2),
(115, 47, ' خروج دم من منطقه الشرج ماسبب ف ذلك مع وجود الم ف المنطقه كأن يوجد براز', NULL, '2021-05-24', 4, 4),
(116, 69, 'السلام عليكم .لدي حكه بسيطه في منطقة الشرج ولا يوجد اي الم ولا يوجد اي دم مع الخروج ولا توجد بروزات خارجة . ولكن احيانا اشعر بوجود بروز في منطقة الشرج .هل لدي بواسير وشكرا', NULL, '2021-05-25', 4, 2),
(117, 88, 'انملت كفوف يدي بلحبوب وقال الطبيب حساسسه دوائيه اجريت تحليل lge والنسبه ٢٤-٦٠ عمري ٢٤ هل يوجد حساسيه في الدم ع ضوء التحليل', NULL, '2021-05-18', 5, 1),
(118, 85, '..عند حساسيه من٧ سنوات الان تحليل الدم نسبه الحساسبه٢٢٥ وانا الا اخذ علاج ولكن با الام وهرش والجلد يكاد يكون محروق وبه جروح الكريمات بموضعيه اريد الحل الله يرضى عايكم انجدونالحساسه', NULL, '2021-05-16', 5, 3),
(119, 65, 'ما بعد تبرع بالدم ماذا يفعل الذي تبرع كيف تخفيف الصداع\r\n', NULL, '2021-05-26', 6, 2),
(120, 72, 'leger prolapsus du feuillet anterieur de la valve mitrale avec im minime هل هذا يعني أن هناك ارتخاء مع ارتجاع في الصمام التاجي؟ما خطوؤة ما قيل؟و هل يجب القيام بالإيكو كل سنة', NULL, '2021-05-25', 7, 2),
(121, 2, 'تحليل الدم والبراز والبول ماهي الامراض التي قد ينتج عنها وهل اذا طلع سليم ؟ اكون سليمه ولا يحتاح تحليل لمرض معين ؟ مثل الكبد والكلى تظهر بالتحاليل العاديه هذي ؟', NULL, '2021-05-04', 8, 1);

--
-- Triggers `post`
