DELIMITER $$
CREATE TRIGGER `post_log_del_trigger` BEFORE DELETE ON `post` FOR EACH ROW INSERT INTO post_log VALUES(
OLD.id ,OLD.user , OLD.content , OLD.img , OLD.date_share , OLD.deptno ,
    OLD.num_search
)
$$
DELIMITER ;

